const menuBtn=document.querySelector(".menu-btn"),sideBar=document.querySelector(".modal.sidebar"),closeBtn=document.querySelector(".close-button"),overlay=document.querySelector(".overlay");let menuOpen=!1;menuBtn.addEventListener("click",()=>{menuOpen?(menuBtn.classList.remove("open"),sideBar.classList.remove("open"),menuOpen=!1):(menuBtn.classList.add("open"),sideBar.classList.add("open"),overlay.classList.add("open"),menuOpen=!0)}),closeBtn.addEventListener("click",()=>{menuBtn.classList.remove("open"),sideBar.classList.remove("open"),overlay.classList.remove("open"),menuOpen=!1}),overlay.addEventListener("click",()=>{menuBtn.classList.remove("open"),sideBar.classList.remove("open"),overlay.classList.remove("open"),menuOpen=!1});